import React from 'react';
import '../Styles/Background.css'
import '../Styles/FormDesign.css'
import Bounce from 'react-reveal/Bounce';
class Login extends React.Component{
    constructor(props) {
        super(props);
        this.state = { show: false };
      }
      handleClick = ()=>{
          this.setState({show:!this.state.show});
          if(this.state.show === false){
              this.props.title = "Login"
          }
          else{
              this.props.title = "Sign-Up"
          }
      }
  render(){    
        return(
            <div>
            <Bounce left opposite when={!this.state.show||this.state.show}>
            <div className='formBack'>
                <h1 className='Title'>{this.props.title}</h1>
                <input type="text" placeholder="Username"></input>
                <input type="password" placeholder="Password"></input>
                <input type="button" value={this.props.title}></input>
            </div>
            </Bounce>
            <p>{this.props.message} <span style={{color:"blue",cursor:"pointer"}} onClick = {this.handleClick}>Click Here</span></p>
            </div>
    );
        }

}

export default Login;
