import React from 'react';
import '../Styles/Background.css'
import '../Styles/FormDesign.css'
import Bounce from 'react-reveal/Bounce';
class Login extends React.Component{
    constructor(props) {
        super(props);
        this.state = { show: false };
      }
      handleClick = ()=>{
          this.setState({show:!this.state.show});
      }
  render(){    
        return(
            <div>
            <Bounce left opposite when={!this.state.show}>
            <div className='formBack'>
                <h1 className='Title'>Login</h1>
                <input type="text" placeholder="Username"></input>
                <input type="password" placeholder="Password"></input>
                <input type="button" value="Login"></input>
                <p>Don't Have Account ? <span style={{color:"blue",cursor:"pointer"}} onClick = {this.handleClick}>Click Here</span></p>
            </div>
            </Bounce>
            <Bounce left opposite when={this.state.show}>
            <div className='formBack'>
                <h1 className='Title'>Sign-In</h1>
                <input type="text" placeholder="Username"></input>
                <input type="password" placeholder="Password"></input>
                <input type="button" value="Create Account"></input>
                <p>You Have Account ? <span style={{color:"blue",cursor:"pointer"}} onClick = {this.handleClick}>Click Here</span></p>
            </div>
            </Bounce>
            </div>
    );
        }

}

export default Login;
