import React from 'react';
import '../Styles/Background.css'
import '../Styles/FormDesign.css'
import Bounce from 'react-reveal/Bounce';
class Login extends React.Component{
    constructor(props) {
        super(props);
        this.state = { show: false ,message:this.props.message,title:this.props.title};
      }
      handleClick = ()=>{
          if(this.state.show === false){
              this.setState({show:true,message:"Have Account ?",title:"Sign-Up"});
          }
          else{
            this.setState({show:false,message:"Don't Have Account ?",title:"Login"});
          }
      }
      componentDidUpdate(){
          setTimeout(() => {
            this.setState({show:false});
          }, 1000);
      }
  render(){    
        return(


            <Bounce left opposite when={!this.state.show}>
            <div className='formBack'>
                <h1 className='Title'>{this.state.title}</h1>
                <input type="text" placeholder="Username"></input>
                <input type="password" placeholder="Password"></input>
                <input type="button" value={this.state.title}></input>
                <p>{this.state.message} <span style={{color:"blue",cursor:"pointer"}} onClick = {this.handleClick}>Click Here</span></p>
            </div>
            </Bounce>


    );
        }

}

export default Login;
